package com.twjitm.core.common.kafka;/** * @author EGLS0807 - [Created on 2018-09-04 21:31] * @company http://www.g2us.com/ * @jdk java version "1.8.0_77" */public abstract class AbstractKafkaTask implements  Runnable{    private KafkaTaskType taskType;    public Object value;    public AbstractKafkaTask(KafkaTaskType taskType) {        this.taskType = taskType;    }    public KafkaTaskType getTaskType() {        return taskType;    }    public Object getValue() {        return value;    }    public abstract void setValue(Object value) ;}