package com.twjitm.core.common.kafka;import org.apache.kafka.clients.consumer.ConsumerRecord;import org.springframework.kafka.listener.MessageListener;/** * kafka 消息消费者 * * @author EGLS0807 - [Created on 2018-09-04 16:12] * @company http://www.g2us.com/ * @jdk java version "1.8.0_77" */public class NettyKafkaConsumerListener implements MessageListener<String, String> {    @Override    public void onMessage(ConsumerRecord<String, String> consumerRecord) {        System.out.println(consumerRecord);        consumerRecord.value();    }}