package com.twjitm.core.common.kafka;import org.springframework.kafka.core.KafkaTemplate;import org.springframework.kafka.support.SendResult;import org.springframework.stereotype.Service;import org.springframework.util.concurrent.ListenableFuture;import javax.annotation.Resource;/** * kafka 消息提供者 * * @author EGLS0807 - [Created on 2018-09-04 16:17] * @company http://www.g2us.com/ * @jdk java version "1.8.0_77" */@Servicepublic class NettyKafkaProducerListener {    @Resource    private KafkaTemplate<KafkaTaskType, Object> kafkaTemplate;    public void sendMessage(AbstractKafkaTask abstractKafkaTask) {        ListenableFuture<SendResult<KafkaTaskType, Object>> result =                kafkaTemplate.sendDefault(abstractKafkaTask.getTaskType(), abstractKafkaTask.getValue());    }}