package com.twjitm.core.service.rpc.service;import com.twjitm.core.common.annotation.NettyRpcServiceAnnotation;import org.springframework.stereotype.Repository;/** * @author EGLS0807 - [Created on 2018-08-20 10:26] * @company http://www.g2us.com/ * @jdk java version "1.8.0_77" */@NettyRpcServiceAnnotation(HelloWorldImpl.class)@Repositorypublic class HelloWorldImpl implements IHelloWorld {    @Override    public String getHelloWorld(int number) {        StringBuilder builder = new StringBuilder();        for (int i = number; i > 0; i--) {            builder.append("helloworld");            builder.append(i);        }        return builder.toString();    }}